mapper.select:
SELECT ITEM_NO, ITEM_TITLE, ITEM_PRICE, ITEM_MAIN_IMG, ITEM_DETAIL_IMG, ITEM_STOCK, ITEM_WRITED_AT FROM ITEM_T
${column} LIKE CONCAT('%', UPPER(#{query}),'%') OR ${column} LIKE CONCAT('%', LOWER(#{query}) , '%')
ORDER BY ITEM_WRITED_AT DESC LIMIT #{begin}, #{recordPerPage}

mapper.insert:
INSERT INTO ITEM_T (
ITEM_TITLE
, ITEM_PRICE
, ITEM_STOCK
, ITEM_MAIN_IMG
, ITEM_DETAIL_IMG
, ITEM_WRITED_AT
) VALUES (
#{itemTitle}
, #{itemPrice}
, #{itemStock}
, #{itemMainImg}
, #{itemDetailImg}
, NOW()
)

mapper.update:
UPDATE ITEM_T
SET
ITEM_TITLE = #{itemTitle}
, ITEM_PRICE = #{itemPrice}
, ITEM_STOCK =
#{itemStock}
, ITEM_MAIN_IMG = #{itemMainImg}
, ITEM_DETAIL_IMG =
#{itemDetailImg}
WHERE ITEM_NO = #{itemNo}

UPDATE ITEM_ORDER_T
SET DELIVERY = #{delivery}
WHERE ITEM_ORDER_NO = #{itemOrderNo}

UPDATE ITEM_T
SET ITEM_STOCK = ITEM_STOCK - #{itemStock}
WHERE ITEM_NO = #{itemNo}

UPDATE ITEM_ORDER_T
SET DELIVERY = '주문취소'
WHERE ITEM_ORDER_NO = #{itemOrderNo}
