mapper.select:
SELECT ITEM_NO, ITEM_TITLE, ITEM_PRICE, ITEM_MAIN_IMG, ITEM_DETAIL_IMG, ITEM_STOCK, ITEM_WRITED_AT FROM ITEM_T
${column} LIKE CONCAT('%', UPPER(#{query}),'%') OR ${column} LIKE CONCAT('%', LOWER(#{query}) , '%')
ORDER BY ITEM_WRITED_AT DESC LIMIT #{begin}, #{recordPerPage}

SELECT ITEM_NO, ITEM_TITLE, ITEM_PRICE, ITEM_MAIN_IMG, ITEM_DETAIL_IMG, ITEM_STOCK, ITEM_WRITED_AT
  FROM ITEM_T
 WHERE ITEM_NO = #{itemNo}
