     , NULL
     , NULL
     , NULL
  )

mapper.select:
SELECT USER_NO, NAME, EMAIL, MOBILE
FROM USER_T
WHERE USER_NO = #{userNo}

SELECT CART_NO, ITEM_NO, USER_NO, QUANTITY, ITEM_TITLE, ITEM_PRICE, ITEM_MAIN_IMG
  FROM CART_T
 WHERE ITEM_NO = #{itemNo}
