mapper.select:
SELECT ITEM_ORDER_NO, USER_NO, ORDER_DATE, NAME, MOBILE, POSTCODE, ROAD_ADDRESS, DETAIL_ADDRESS, ORDER_TOTAL, PAY_METHOD, DELIVERY
FROM ITEM_ORDER_T
WHERE USER_NO = #{userNo}

SELECT USER_NO, NAME, EMAIL, MOBILE
FROM USER_T
WHERE USER_NO = #{userNo}

SELECT CART_NO, ITEM_NO, USER_NO, QUANTITY, ITEM_TITLE, ITEM_PRICE, ITEM_MAIN_IMG
  FROM CART_T
 WHERE ITEM_NO = #{itemNo}
