mapper.insert:
INSERT INTO USER_T (
    ID
  , PW
  , NAME
  , GENDER
  , EMAIL
  , MOBILE
  , BIRTHYEAR
  , BIRTHDATE
  , POSTCODE
  , ROAD_ADDRESS
  , JIBUN_ADDRESS
  , DETAIL_ADDRESS
  , EXTRA_ADDRESS
  , AGREECODE
  , JOINED_AT
  , PW_MODIFIED_AT
  , AUTOLOGIN_ID
  , AUTOLOGIN_EXPIRED_AT
  , ADMIN_CHECK
) VALUES (
    #{id}
  , #{pw}
  , #{name}
  , #{gender}
  , #{email}
  , #{mobile}
  , #{birthyear}
  , #{birthdate}
  , #{postcode}
  , #{roadAddress}
  , #{jibunAddress}
  , #{detailAddress}
  , #{extraAddress}
  , #{agreecode}
  , NOW()
  , NOW()
  , NULL
  , NULL
  , #{adminCheck}
)

mapper.select:
SELECT ID
  FROM SLEEP_USER_T
 WHERE ID = #{id}

SELECT ID
  FROM OUT_USER_T
 WHERE ID = #{id}

SELECT EMAIL
  FROM USER_T
 WHERE EMAIL = #{email}

SELECT EMAIL
  FROM SLEEP_USER_T
 WHERE EMAIL = #{email}

SELECT EMAIL
  FROM OUT_USER_T
 WHERE EMAIL = #{email}

SELECT ID
     , ADMIN_CHECK
  FROM USER_T
 WHERE ID = #{id}
   AND PW = #{pw}

mapper.update:
UPDATE USER_ACCESS_T
   SET LAST_LOGIN_AT = NOW()
 WHERE ID = #{id}

mapper.insert:
INSERT INTO USER_ACCESS_T (
    ID
  , LAST_LOGIN_AT
) VALUES (
    #{id}
  , NOW()
)

mapper.update:
UPDATE USER_T
   SET AUTOLOGIN_ID = #{autologinId}
     , AUTOLOGIN_EXPIRED_AT = #{autologinExpiredAt}
 WHERE ID = #{id}
